import React, { useEffect, useState } from 'react';
import { ethers, parseEther, formatEther } from 'ethers';
import { contractAbi } from './Constant/constant.js';
import { contractAddress } from './Constant/constant.js';

function App() {
    const [provider, setProvider] = useState(null);
    const [signer, setSigner] = useState(null);
    const [contract, setContract] = useState(null);
    const [account, setAccount] = useState(null);
    const [posts, setPosts] = useState([]);  // Array to store posts

    useEffect(() => {
        let isInitializing = false;

        const init = async () => {
            if (isInitializing) return;
            isInitializing = true;

            const { ethereum } = window;

            if (!ethereum) {
                console.error("MetaMask is not installed");
                isInitializing = false;
                return;
            }

            try {
                await ethereum.request({ method: 'eth_requestAccounts' });

                const provider = new ethers.BrowserProvider(ethereum);
                const signer = await provider.getSigner();
                const contract = new ethers.Contract(contractAddress, contractAbi, signer);

                setProvider(provider);
                setSigner(signer);
                setContract(contract);
                setAccount(signer.address);

                // Load all posts when the app initializes
                await loadPosts(contract);
            } catch (error) {
                console.error('Error initializing Ethereum connection:', error);
            } finally {
                isInitializing = false;
            }
        };

        init();
    }, []);

    const loadPosts = async (contract) => {
        try {
            const postCount = await contract.getPostCount();
            const postsArray = [];
            for (let i = 0; i < postCount; i++) {
                const post = await contract.getPost(i);
                postsArray.push({
                    id: i,
                    goalAmount: formatEther(post.goalAmount),
                    minContribution: formatEther(post.minContribution),
                    collectedFunds: formatEther(post.collectedFunds),
                    isActive: post.isActive,
                });
            }
            setPosts(postsArray);
        } catch (err) {
            console.error('Error loading posts:', err);
        }
    };

    const createPost = async (goalAmount, minContribution) => {
        if (!contract) return;

        try {
            const tx = await contract.createPost(
                parseEther(goalAmount),
                parseEther(minContribution)
            );
            await tx.wait();
            console.log('Post created successfully!');
            await loadPosts(contract);  // Reload posts after creation
        } catch (err) {
            console.error('Error creating post:', err);
        }
    };

    const fundPost = async (postId, amount) => {
        if (!contract) return;

        try {
            const tx = await contract.fundPost(postId, {
                value: parseEther(amount),
            });
            await tx.wait();
            console.log('Funded post successfully!');
            await loadPosts(contract);  // Reload posts after funding
        } catch (err) {
            console.error('Error funding post:', err);
        }
    };

    const getCollectedFunds = async (postId) => {
        if (!contract) return;

        try {
            const collectedFunds = await contract.getCollectedFunds(postId);
            console.log(`Collected funds: ${formatEther(collectedFunds)} ETH`);
        } catch (err) {
            console.error('Error getting collected funds:', err);
        }
    };

    const handleCreatePost = (e) => {
        e.preventDefault();
        createPost(goalAmount, minContribution);
    };

    const handleFundPost = (e) => {
        e.preventDefault();
        fundPost(postId, fundAmount);
    };

    const handleGetCollectedFunds = (e) => {
        e.preventDefault();
        getCollectedFunds(postId);
    };

    return (
        <div>
            <h1>Crowdfunding DApp</h1>
            <p>Connected account: {account}</p>

            <form onSubmit={handleCreatePost}>
                <input
                    type="text"
                    placeholder="Goal Amount (ETH)"
                    value={goalAmount}
                    onChange={(e) => setGoalAmount(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="Minimum Contribution (ETH)"
                    value={minContribution}
                    onChange={(e) => setMinContribution(e.target.value)}
                />
                <button type="submit">Create Post</button>
            </form>

            <form onSubmit={handleFundPost}>
                <input
                    type="text"
                    placeholder="Post ID"
                    value={postId}
                    onChange={(e) => setPostId(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="Fund Amount (ETH)"
                    value={fundAmount}
                    onChange={(e) => setFundAmount(e.target.value)}
                />
                <button type="submit">Fund Post</button>
            </form>

            <form onSubmit={handleGetCollectedFunds}>
                <input
                    type="text"
                    placeholder="Post ID"
                    value={postId}
                    onChange={(e) => setPostId(e.target.value)}
                />
                <button type="submit">Get Collected Funds</button>
            </form>

            <h2>All Posts</h2>
            <ul>
                {posts.map((post, index) => (
                    <li key={index}>
                        <h3>Post ID: {post.id}</h3>
                        <p>Goal Amount: {post.goalAmount} ETH</p>
                        <p>Minimum Contribution: {post.minContribution} ETH</p>
                        <p>Collected Funds: {post.collectedFunds} ETH</p>
                        <p>Status: {post.isActive ? 'Active' : 'Inactive'}</p>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default App;
