import React, { useEffect, useState } from 'react';
import { ethers, parseEther, formatEther } from 'ethers';
import { contractAbi } from './Constant/constant.js';
import { contractAddress } from './Constant/constant.js';

function App() {
    const [provider, setProvider] = useState(null);
    const [signer, setSigner] = useState(null);
    const [contract, setContract] = useState(null);
    const [posts, setPosts] = useState([]);

    useEffect(() => {
        async function init() {
            const provider = new ethers.providers.Web3Provider(window.ethereum);
            const signer = provider.getSigner();
            const contract = new ethers.Contract(contractAddress, CrowdFundingABI, signer);

            setProvider(provider);
            setSigner(signer);
            setContract(contract);
        }

        init();
    }, []);

    const createPost = async (goalAmount, minContribution) => {
        try {
            const tx = await contract.createPost(goalAmount, minContribution);
            await tx.wait();
            alert("Post created successfully!");
        } catch (error) {
            console.error("Error creating post:", error);
            alert("Failed to create post");
        }
    };

    const fundPost = async (postId, amount) => {
        try {
            const tx = await contract.fundPost(postId, { value: ethers.utils.parseEther(amount.toString()) });
            await tx.wait();
            alert("Funded post successfully!");
        } catch (error) {
            console.error("Error funding post:", error);
            alert("Failed to fund post");
        }
    };

    const checkDeadline = async (postId) => {
        try {
            const tx = await contract.checkDeadline(postId);
            await tx.wait();
            alert("Checked deadline successfully!");
        } catch (error) {
            console.error("Error checking deadline:", error);
            alert("Failed to check deadline");
        }
    };

    const claimRefund = async (postId) => {
        try {
            const tx = await contract.claimRefund(postId);
            await tx.wait();
            alert("Refund claimed successfully!");
        } catch (error) {
            console.error("Error claiming refund:", error);
            alert("Failed to claim refund");
        }
    };

    const fetchPosts = async () => {
        try {
            const postIds = await contract.getPostIds();
            const postsData = await Promise.all(postIds.map(async (postId) => {
                const post = await contract.getPost(postId);
                return { postId, ...post };
            }));
            setPosts(postsData);
        } catch (error) {
            console.error("Error fetching posts:", error);
            alert("Failed to fetch posts");
        }
    };

    useEffect(() => {
        if (contract) {
            fetchPosts();
        }
    }, [contract]);

    return (
        <div>
            <h1>Crowdfunding DApp</h1>

            <h2>Create a new post</h2>
            <input type="number" placeholder="Goal Amount" id="goalAmount" />
            <input type="number" placeholder="Min Contribution" id="minContribution" />
            <button onClick={() => createPost(
                document.getElementById('goalAmount').value,
                document.getElementById('minContribution').value
            )}>Create Post</button>

            <h2>All Posts</h2>
            {posts.map(post => (
                <div key={post.postId}>
                    <h3>Post ID: {post.postId}</h3>
                    <p>Creator: {post.creator}</p>
                    <p>Goal Amount: {ethers.utils.formatEther(post.goalAmount)} ETH</p>
                    <p>Collected Amount: {ethers.utils.formatEther(post.collectedAmount)} ETH</p>
                    <p>Min Contribution: {ethers.utils.formatEther(post.minContribution)} ETH</p>
                    <p>Deadline: {new Date(post.deadline * 1000).toLocaleString()}</p>
                    <p>Status: {post.active ? "Active" : "Inactive"}</p>
                    {post.active && (
                        <div>
                            <input type="number" placeholder="Amount" id={`fundAmount-${post.postId}`} />
                            <button onClick={() => fundPost(
                                post.postId,
                                document.getElementById(`fundAmount-${post.postId}`).value
                            )}>Fund Post</button>
                            <button onClick={() => checkDeadline(post.postId)}>Check Deadline</button>
                        </div>
                    )}
                    {!post.active && post.collectedAmount < post.goalAmount && (
                        <button onClick={() => claimRefund(post.postId)}>Claim Refund</button>
                    )}
                </div>
            ))}
        </div>
    );
}

export default App;
