import React, { useEffect, useState } from 'react';
import { BrowserProvider,Contract, parseEther,formatEther} from 'ethers';
import { contractAbi } from './Constant/constant.js';
import { contractAddress } from './Constant/constant.js';

function App() {
    const [provider, setProvider] = useState(null);
    const [signer, setSigner] = useState(null);
    const [contract, setContract] = useState(null);
    const [currentAccount, setCurrentAccount] = useState(null);
    const [fundingAmount, setFundingAmount] = useState("");
    const [fundsRaised, setCollctedFunds] = useState("0");

    useEffect(() => {
        const init = async () => {
            if (window.ethereum) {
                const browserProvider = new BrowserProvider(window.ethereum);
                setProvider(browserProvider);

                const signer = await browserProvider.getSigner();
                setSigner(signer);

                const contract = new Contract(contractAddress, contractAbi, signer);
                setContract(contract);

                const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
                setCurrentAccount(accounts[0]);

                // Fetch initial funds raised
                const initialFundsRaised = await contract.getCollectedFunds(); // Assuming fundsRaised is a public state variable
                setCollctedFunds(formatEther(initialFundsRaised));
            } else {
                console.log("Please install MetaMask!");
            }
        };

        init();
    }, []);

    const handleFundProject = async () => {
        if (!signer || !contract) return;

        try {
            const valueInEther = parseEther(fundingAmount);
            const transaction = await contract.fundProject({ value: valueInEther });
            await transaction.wait(); // Wait for the transaction to be confirmed
            console.log('Transaction:', transaction);

            // Update the funds raised after the transaction
            const updatedFundsRaised = await contract.fundsRaised();
            setCollctedFunds(formatEther(updatedFundsRaised));
        } catch (error) {
            console.error("Transaction Error:", error);
        }
    };

    const handleRefund = async () => {
        if (!signer || !contract) return;

        try {
            const transaction = await contract.refund();
            await transaction.wait(); // Wait for the transaction to be confirmed
            console.log('Refund Transaction:', transaction);

            // Update the funds raised after the refund
            const updatedFundsRaised = await contract.fundsRaised();
            setCollctedFunds(formatEther(updatedFundsRaised));
        } catch (error) {
            console.error("Refund Error:", error);
        }
    };

    const handleInputChange = (event) => {
        setFundingAmount(event.target.value);
    };

    return (
        <div>
            <h1>Crowdfunding DApp</h1>
            <p>Connected Account: {currentAccount}</p>
            <p>Funds Raised: {fundsRaised} ETH</p>
            <input 
                type="text" 
                value={fundingAmount} 
                onChange={handleInputChange} 
                placeholder="Enter amount to fund" 
            />
            <button onClick={handleFundProject}>Fund Project</button>
            <button onClick={handleRefund}>Request Refund</button>
        </div>
    );
}

export default App;
