import React, { useEffect, useState } from 'react';
import { BrowserProvider,Contract, parseEther,formatEther} from 'ethers';
import { contractAbi } from './Constant/constant.js';
import { contractAddress } from './Constant/constant.js';

function App() {
    const [provider, setProvider] = useState(null);
    const [signer, setSigner] = useState(null);
    const [contract, setContract] = useState(null);
    const [currentAccount, setCurrentAccount] = useState(null);
    const [fundingAmount, setFundingAmount] = useState("");
    const [fundsRaised, setFundsRaised] = useState("0");

    useEffect(() => {
        const init = async () => {
            // Check if MetaMask is installed
            if (window.ethereum) {
                const browserProvider = new BrowserProvider(window.ethereum);
                setProvider(browserProvider);

                const signer = await browserProvider.getSigner();
                setSigner(signer);

                const contract = new Contract(contractAddress, CrowdFundingABI, signer);
                setContract(contract);

                const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
                setCurrentAccount(accounts[0]);
            } else {
                console.log("Please install MetaMask!");
            }
        };

        init();
    }, []);

    const handleTransaction = async () => {
        if (!signer || !contract) return;

        try {
            const valueInEther = parseEther(fundingAmount); // Use parseEther directly
            const transactionHash = await signer.sendTransaction({
                to: contractAddress,
                value: valueInEther,
            });
            console.log('Transaction Hash:', transactionHash);

            // Optionally update the funds raised state
            const fundsRaised = await contract.fundsRaised(); // Assuming this is a method in your smart contract
            setFundsRaised(formatEther(fundsRaised)); // Convert funds raised to Ether using formatEther
        } catch (error) {
            console.error("Transaction Error:", error);
        }
    };

    const handleInputChange = (event) => {
        setFundingAmount(event.target.value);
    };

    return (
        <div>
            <h1>Crowdfunding DApp</h1>
            <p>Connected Account: {currentAccount}</p>
            <p>Funds Raised: {fundsRaised} ETH</p>
            <input 
                type="text" 
                value={fundingAmount} 
                onChange={handleInputChange} 
                placeholder="Enter amount to fund" 
            />
            <button onClick={handleTransaction}>Fund Project</button>
        </div>
    );
}

export default App;
